    private void jTextUsuarioKeyPressed(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_jTextUsuarioKeyPressed
        if (evt.getKeyCode() == KeyEvent.VK_ENTER) {
            jFieldSenha.requestFocus();
        }
    }//GEN-LAST:event_jTextUsuarioKeyPressed

    private void jFieldSenhaKeyPressed(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_jFieldSenhaKeyPressed
        if (evt.getKeyCode() == KeyEvent.VK_ENTER) {
            jButtonEntrar.requestFocus();
        }
    }//GEN-LAST:event_jFieldSenhaKeyPressed

